import React, { useEffect, useState } from "react";

// ‚úÖ Ìä∏Î¶¨ Ïª¥Ìè¨ÎÑåÌä∏
const PageTree = ({ page, depth = 0 }) => {
  const [expanded, setExpanded] = useState(false);
  const hasChildren = page.pages && page.pages.length > 0;

  const clickupUrl = `https://app.clickup.com/${page.workspace_id}/v/dc/${page.doc_id}/${page.id}`;

  return (
    <div style={{ paddingLeft: depth * 20 }}>
      {hasChildren ? (
        <span
          onClick={() => setExpanded(!expanded)}
          style={{ cursor: "pointer", fontWeight: "bold" }}
        >
          {expanded ? "üìÇ" : "üìÅ"} {page.name}
        </span>
      ) : (
        <a
          href={clickupUrl}
          target="_blank"
          rel="noopener noreferrer"
          style={{ textDecoration: "none", fontWeight: "bold", color: "#2a5bd7" }}
        >
          üìÑ {page.name}
        </a>
      )}

      {expanded && hasChildren && (
        <div>
          {page.pages.map((subPage) => (
            <PageTree key={subPage.id} page={subPage} depth={depth + 1} />
          ))}
        </div>
      )}
    </div>
  );
};

// ‚úÖ Ïû¨Í∑Ä ÌÉêÏÉâ Ìï®Ïàò
const findNodeById = (nodes, targetId) => {
  if (!Array.isArray(nodes)) return null;
  for (const node of nodes) {
    if (node.id === targetId) return node;
    if (node.pages?.length > 0) {
      const found = findNodeById(node.pages, targetId);
      if (found) return found;
    }
  }
  return null;
};

function App() {
  const [config, setConfig] = useState(null);
  const [treeData, setTreeData] = useState(null);

  useEffect(() => {
  fetch("/config.json")
    .then((res) => res.json())
    .then((cfg) => {
      console.log("‚úÖ config Î°úÎî©Îê®", cfg); // Ïó¨Í∏∞ Ï∂îÍ∞Ä
      setConfig(cfg);

      const options = {
        method: "GET",
        headers: {
          accept: "application/json",
          Authorization: cfg.apiToken,
        },
      };

      const url = `https://api.clickup.com/api/v3/workspaces/${cfg.workspaceId}/docs/${cfg.docId}/pageListing?max_page_depth=10`;
      console.log("üì° API ÏöîÏ≤≠ URL:", url);

      return fetch(url, options);
    })
    .then((res) => res.json())
    .then((apiData) => {
      console.log("üì¶ API ÏùëÎãµ:", apiData); // ÏùëÎãµ ÌôïÏù∏
      setTreeData(apiData.pages);
    })
    .catch((err) => {
      console.error("‚ùå API fetch error:", err);
    });
}, []);

  if (!config || !treeData) return <p>üîÑ Î°úÎî© Ï§ë...</p>;

  const root = findNodeById(treeData, config.rootId);
  if (!root) return <p>‚ùå ID {config.rootId}Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.</p>;

  return (
    <div className="App">
      <h2>üìö ClickUp Î¨∏ÏÑú Ìä∏Î¶¨ Î∑∞Ïñ¥</h2>
      <PageTree page={root} />
    </div>
  );
}

export default App;
