import React, { useEffect, useState } from "react";

// üóÇÔ∏è Ìä∏Î¶¨ Î†åÎçîÎßÅ Ïª¥Ìè¨ÎÑåÌä∏
const PageTree = ({ page, depth = 0 }) => {
  const [expanded, setExpanded] = useState(false);
  const hasChildren = page.pages && page.pages.length > 0;
  const clickupUrl = `https://app.clickup.com/${page.workspace_id}/v/dc/${page.doc_id}/${page.id}`;

  return (
    <div style={{ paddingLeft: depth * 20 }}>
      {hasChildren ? (
        <span
          onClick={() => setExpanded(!expanded)}
          style={{ cursor: "pointer", fontWeight: "bold" }}
        >
          {expanded ? "üìÇ" : "üìÅ"} {page.name}
        </span>
      ) : (
        <a
          href={clickupUrl}
          target="_blank"
          rel="noopener noreferrer"
          style={{ textDecoration: "none", fontWeight: "bold", color: "#2a5bd7" }}
        >
          üìÑ {page.name}
        </a>
      )}

      {expanded && hasChildren && (
        <div>
          {page.pages.map((subPage) => (
            <PageTree key={subPage.id} page={subPage} depth={depth + 1} />
          ))}
        </div>
      )}
    </div>
  );
};

// üîç ÌäπÏ†ï IDÎ°ú ÎÖ∏Îìú Ï∞æÍ∏∞
const findNodeById = (nodes, targetId) => {
  for (const node of nodes) {
    if (node.id === targetId) return node;
    if (node.pages?.length > 0) {
      const found = findNodeById(node.pages, targetId);
      if (found) return found;
    }
  }
  return null;
};

function App() {
  const [treeData, setTreeData] = useState(null);
  const [rootId, setRootId] = useState(null);

  useEffect(() => {
    let ignore = false;

    const loadData = async () => {
      try {
        const res = await fetch("http://localhost:4000/clickup-data");
        const data = await res.json();
        if (!ignore) {
          setTreeData(data.pages);
          setRootId(data.rootId);
        }
      } catch (err) {
        console.error("‚ùå fetch error:", err);
      }
    };

    loadData();

    return () => {
      ignore = true; // cleanup Ìï®Ïàò: Ï§ëÎ≥µ Î∞©ÏßÄ
    };
  }, []);

  if (!treeData || !rootId) return <p>üîÑ Î°úÎî© Ï§ë...</p>;

  const root = findNodeById(treeData, rootId);
  if (!root) return <p>‚ùå ID {rootId}Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.</p>;

  return (
    <div className="App">
      <h2>üìö ClickUp Î¨∏ÏÑú Ìä∏Î¶¨ Î∑∞Ïñ¥</h2>
      <PageTree page={root} />
    </div>
  );
}

export default App;
